INSERT INTO beaches ("title", "description", "imageUrl", "text", "type") VALUES
                                                                             ('Ilica Beach', 'Měkký bílý písek a mělká termální voda', '/images/ilica.webp',
                                                                              'Ilıca Beach u Çeşme má cca 1,5 km dlouhý pás jemného bílého písku. Voda je mělká, teplá a v některých místech termální prameny vstupují přímo do moře. Pláž je ideální pro rodiny s dětmi i windsurfingové nadšence. ([turn0search6],[turn0search5])', 'písek'),

                                                                             ('Altinkum Beach', 'Široký zlatý písek u Didimu', '/images/altinkum.webp',
